import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import TableDataContext from './TableDataContext';

function TableDataProvider({ children }) {
  const [data, setData] = useState(null);
  const fetchDataFromApi = async () => {
    const request = await fetch('https://swapi-trybe.herokuapp.com/api/planets/?format=json');
    const response = await request.json();
    const result = await response.results.map((planet) => {
      delete planet.residents;
      return planet;
    });
    console.log(result);
    setData(result);
  };
  useEffect(() => {
    fetchDataFromApi();
  }, []);
  return (
    <TableDataContext.Provider value={ { data } }>
      {children}
    </TableDataContext.Provider>
  );
}

TableDataProvider.propTypes = {
  children: PropTypes.node.isRequired,
};
export default TableDataProvider;
